import { useState, useEffect } from "react";
import "./Dashboard.scss";
import { jwtDecode } from "jwt-decode";
import Messages from "./../message/Message";
import { Link, useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { sessionStorage } from "react"; // Dodane

const Dashboard = () => {
  const [userInfo, setUserInfo] = useState(null);
  const [receivedMessages, setReceivedMessages] = useState([]);
  const [sentMessages, setSentMessages] = useState([]);
  const navigate = useNavigate();
  const { userId } = useParams();

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const token = sessionStorage.getItem("token");

        if (token) {
          const receivedResponse = await axios.get(`/users/messages/received`, {
            headers: { Authorization: `Bearer ${token}` },
          });
          setReceivedMessages(receivedResponse.data.messages);

          const sentResponse = await axios.get(`/users/messages/send`, {
            headers: { Authorization: `Bearer ${token}` },
          });
          setSentMessages(sentResponse.data.messages);
        }
      } catch (error) {
        console.error(
          "Błąd podczas pobierania wiadomości:",
          error.response.data
        );
      }
    };

    fetchMessages();
  }, [userId]);

  useEffect(() => {
    const token = sessionStorage.getItem("token");

    if (token) {
      const decodedToken = jwtDecode(token);
      setUserInfo(decodedToken);
    }
  }, []);

  const handleLogout = () => {
    // Usuń token i rolę użytkownika z sessionStorage
    sessionStorage.removeItem("token");
    sessionStorage.removeItem("userRole");
    navigate("/");
  };

  return (
    <div className="dashboard-container">
      <div className="user-info">
        {userInfo && (
          <p>
            Zalogowany użytkownik:{" "}
            {userInfo.admin ? "Admin" : "Standardowy użytkownik"}
          </p>
        )}
        <button onClick={handleLogout}>Wyloguj</button>{" "}
      </div>
      <div className="received-mess">
        <h1>Odebrane</h1>
        <Messages userId={userId} messages={receivedMessages} />
        <div className="messages"></div>
      </div>
      <div className="send-mess">
        <Link to="/newmessage">Nowa Wiadomość</Link>
      </div>
      <div className="sent-mess">
        <h1>Wysłane</h1>
        <Messages userId={userId} messages={sentMessages} />
        <div className="messages"></div>
      </div>
    </div>
  );
};

export default Dashboard;
