import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const NewMessage = ({ userId }) => {
  const [recipient, setRecipient] = useState("");
  const [content, setContent] = useState("");
  const [successMessage, setSuccessMessage] = useState(null);
  const [error, setError] = useState(null);
  const [usersList, setUsersList] = useState([]);

  useEffect(() => {
    const fetchUsersList = async () => {
      try {
        const response = await axios.get("/api/user/login");
        setUsersList(response.data.users);
      } catch (error) {
        setError("Błąd podczas pobierania listy użytkowników");
        console.error(error.response ? error.response.data : error.message);
      }
    };

    fetchUsersList();
  }, []);

  const handleSendMessage = async () => {
    try {
      await axios.post(`/users/messages/send`, {
        sender: userId,
        recipient,
        content,
      });

      setSuccessMessage("Wiadomość została wysłana pomyślnie.");
    } catch (error) {
      setError("Błąd podczas wysyłania wiadomości");
      console.error(error.response ? error.response.data : error.message);
    }
  };

  return (
    <div>
      <h2>Nowa Wiadomość</h2>
      {error && <div style={{ color: "red" }}>{error}</div>}
      {successMessage && <div style={{ color: "green" }}>{successMessage}</div>}
      <label>
        Odbiorca:
        <input
          type="text"
          list="users"
          value={recipient}
          onChange={(e) => setRecipient(e.target.value)}
        />
        <datalist id="users">
          {usersList &&
            usersList.map((user) => (
              <option key={user.id} value={user.username} />
            ))}
        </datalist>
      </label>
      <br />
      <label>
        Treść:
        <textarea
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
      </label>
      <br />
      <button onClick={handleSendMessage}>Wyślij</button>
    </div>
  );
};

export default NewMessage;
