import { useState, useEffect } from "react";
import "./Dashboard.scss";
import { jwtDecode } from "jwt-decode";
import Messages from "./../message/Message";
import { Link } from "react-router-dom";

const Dashboard = ({ userId }) => {
  const [userInfo, setUserInfo] = useState(null);
  const [receivedMessages, setReceivedMessages] = useState([]);
  const [sentMessages, setSentMessages] = useState([]);

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        // Pobierz otrzymane wiadomości
        const receivedResponse = await axios.get(
          `/users/${userId}/messages/received`
        );
        setReceivedMessages(receivedResponse.data.messages);

        // Pobierz wysłane wiadomości
        const sentResponse = await axios.get(`/users/${userId}/messages/sent`);
        setSentMessages(sentResponse.data.messages);
      } catch (error) {
        console.error("Błąd podczas pobierania wiadomości:", error);
      }
    };

    fetchMessages();
  }, [userId]);

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (token) {
      const decodedToken = jwtDecode(token);

      setUserInfo(decodedToken);
    }
  }, []);

  return (
    <div className="dashboard-container">
      <div className="user-info">
        {userInfo && (
          <p>
            Zalogowany użytkownik:{" "}
            {userInfo.admin ? "Admin" : "Standardowy użytkownik"}
          </p>
        )}
      </div>
      <div className="received-mess">
        <h1>Odebrane</h1>
        <Messages userId={userId} />
        <div className="messages"></div>
      </div>
      <div className="send-mess">
        <Link to="/new-message">Nowa Wiadomość</Link>
      </div>
      <div className="sent-mess">
        <h1>Wysłane</h1>
        <Messages userId={userId} />
        <div className="messages"></div>
      </div>
    </div>
  );
};

export default Dashboard;
