import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./NewMessage.scss";

const NewMessage = ({ userId }) => {
  const [selectedRecipient, setSelectedRecipient] = useState("");
  const [content, setContent] = useState("");
  const [successMessage, setSuccessMessage] = useState(null);
  const [error, setError] = useState(null);
  const [usersList, setUsersList] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get(
          `/api/users?token=${sessionStorage.getItem("token")}`
        );
        setUsers(response.data.users);
      } catch (error) {
        console.error("Error fetching users", error);
      }
    };

    fetchUsers();
  }, []);

  useEffect(() => {
    // Filtruj użytkowników na podstawie wpisanego tekstu
    setFilteredUsers(
      usersList.filter((user) =>
        user.username.toLowerCase().includes(selectedRecipient.toLowerCase())
      )
    );
  }, [selectedRecipient, usersList]);

  const handleSendMessage = async () => {
    try {
      await axios.post(`/users/messages/send`, {
        sender: userId,
        recipient: selectedRecipient,
        content,
      });

      setSuccessMessage("Wiadomość została wysłana pomyślnie.");
    } catch (error) {
      setError("Błąd podczas wysyłania wiadomości");
      console.error(error.response ? error.response.data : error.message);
    }
  };

  return (
    <div className="new-message-container">
      <h2>Nowa Wiadomość</h2>
      {error && <div className="error-msg">{error}</div>}
      {successMessage && <div className="success-msg">{successMessage}</div>}
      <label className="message-label">
        Odbiorca:
        <input
          type="text"
          list="users"
          value={selectedRecipient}
          onChange={(e) => setSelectedRecipient(e.target.value)}
        />
        <datalist id="users">
          {filteredUsers.map((user) => (
            <option key={user.id} value={user.username} />
          ))}
        </datalist>
      </label>
      <br />
      <label className="message-label">
        Treść:
        <textarea
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
      </label>
      <br />
      <button className="send-btn" onClick={handleSendMessage}>
        Wyślij
      </button>
      <button className="back-btn" onClick={() => navigate("/dashboard")}>
        Powrót
      </button>
    </div>
  );
};

export default NewMessage;
