import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Dashboard.scss";

const Dashboard = () => {
  const [messagesSent, setMessagesSent] = useState([]);
  const [messagesArrived, setMessagesArrived] = useState([]);
  const [loggedInUser, setLoggedInUser] = useState(null);

  const [users, setUsers] = useState([]);
  const getMessages = async () => {
    try {
      const response = await axios.get(
        `/api/messages?token=${sessionStorage.getItem("token")}`
      );
      const data = response.data;

      if (data.success) {
        setMessagesArrived(data.messagesArrived);
        setMessagesSent(data.messagesSent);
      }
    } catch (error) {
      console.error("Error fetching messages", error);
    }
  };
  const deleteMessage = async (id) => {
    try {
      const response = await axios.delete(
        `/api/messages/${id}?token=${sessionStorage.getItem("token")}`
      );
      const data = response.data;

      if (data.success) {
        getMessages();
      }
    } catch (error) {
      console.error("Error deleting message", error);
    }
  };

  const sendMessage = async (e) => {
    const form = e.target.parentNode;
    const recipient = form[0].value;
    const title = form[1].value;
    const content = form[2].value;
    if (!recipient || !content) {
      alert("Uzupełnij dane w formularzu");
      return;
    }

    const message = {
      recipient: recipient,
      title: title,
      content: content,
      read: false,
      sentAt: new Date(),
    };
    const response = await axios.post(
      `/api/messages?token=${sessionStorage.getItem("token")}`,
      message
    );
    const data = response.data;

    if (data.success) {
      getMessages();
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await axios.get(
        `/api/users?token=${sessionStorage.getItem("token")}`
      );
      setUsers(response.data.users);
    } catch (error) {
      console.error("Error fetching users", error);
    }
  };
  useEffect(() => {
    const navigate = useNavigate();
    const token = sessionStorage.getItem("token");
    console.log(token);
    if (!token) {
      navigate("/");
    } else getMessages();
    setLoggedInUser(sessionStorage.getItem("loggedInUser"));
  }, []);

  return (
    <div className="dashboard-container">
      <div>
        <button
          onClick={() => {
            sessionStorage.removeItem("token");
            sessionStorage.removeItem("loggedInUser");
            navigate("/");
          }}
        >
          Wyloguj
        </button>
      </div>
      <p>Jesteś zalogowany jako: {loggedInUser}</p>

      <form className="message-form">
        <select
          className="message-form-select"
          onFocus={fetchUsers}
          placeholder="Recipient..."
        >
          <option value="" disabled selected hidden>
            Wybierz odbiorce
          </option>
          {users
            .filter((user) => user.email !== loggedInUser)
            .map((user) => (
              <option key={user._id} value={user.email}>
                {user.email}
              </option>
            ))}
        </select>
        <input
          className="message-form-input"
          type="text"
          placeholder="Tytuł wiadomości..."
        />
        <input
          className="message-form-input"
          type="text"
          placeholder="Tekst wiadomości..."
        />
        <button
          className="message-form-button"
          type="submit"
          onClick={(e) => {
            e.preventDefault();
            sendMessage(e);
          }}
        >
          Send
        </button>
      </form>
      <div class="dashboard-container">
        {(messagesSent.length > 0 || messagesArrived.length > 0) && (
          <div class="mail-container">
            <div className="refresh-messages">
              <button onClick={() => getMessages()}>Odśwież wiadomości</button>
            </div>

            <h2>Wiadomości wysłane</h2>
            {messagesSent.map((message) => (
              <div key={message._id} class="mail-row">
                <div class="mail-recipient">{message.recipient}</div>
                <div class="mail-message">
                  <strong>{message.title}</strong> - {message.content}
                </div>
                <div class="mail-status">
                  {message.read ? (
                    <span style={{ color: "green" }}>✔</span>
                  ) : (
                    <span style={{ color: "red" }}>✖</span>
                  )}
                </div>
                <div class="mail-date">{message.sentAt}</div>
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    deleteMessage(message._id);
                  }}
                >
                  Usuń
                </button>
              </div>
            ))}

            <h2>Wiadomości odebrane</h2>
            {messagesArrived.map((message) => (
              <div key={message._id} class="mail-row">
                <div class="mail-sender">{message.sender}</div>
                <div class="mail-message">
                  <strong>{message.title}</strong> - {message.content}
                </div>
                <div class="mail-status">
                  {message.read ? (
                    <span style={{ color: "green" }}>✔</span>
                  ) : (
                    <span style={{ color: "red" }}>✖</span>
                  )}
                </div>
                <div class="mail-date">{message.sentAt}</div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
