import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./NewMessage.scss";

const NewMessage = ({ userId }) => {
  const [selectedRecipient, setSelectedRecipient] = useState("");
  const [content, setContent] = useState("");
  const [successMessage, setSuccessMessage] = useState(null);
  const [error, setError] = useState(null);
  const [usersList, setUsersList] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [availableRecipients, setAvailableRecipients] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = sessionStorage.getItem("token");
    const fetchUsersList = async () => {
      try {
        const response = await axios.get("/api/users", { params: { token } });
        setUsersList(response.data.users);
      } catch (error) {
        setError("Błąd podczas pobierania listy użytkowników");
        console.error(error.response ? error.response.data : error.message);
      }
    };

    fetchUsersList();
  }, []);

  useEffect(() => {
    // Filtruj użytkowników na podstawie wpisanego tekstu
    if (usersList) {
      setFilteredUsers(
        usersList.filter((user) =>
          user.username.toLowerCase().includes(selectedRecipient.toLowerCase())
        )
      );
    }
  }, [selectedRecipient, usersList]);

  const handleSendMessage = async () => {
    try {
      const token = sessionStorage.getItem("token");

      // Upewnij się, że odbiorca to adres e-mailowy
      const recipient = filteredUsers.find(
        (user) => user.username === selectedRecipient
      )?.email;

      if (!recipient) {
        setError("Nieprawidłowy odbiorca");
        return;
      }

      // Użyj endpointu "/api/messages" zgodnego z serwerem
      await axios.post("/api/messages", {
        token,
        sender: userId,
        recipient,
        content,
      });

      setSuccessMessage("Wiadomość została wysłana pomyślnie.");
    } catch (error) {
      setError("Błąd podczas wysyłania wiadomości");
      console.error(error.response ? error.response.data : error.message);
    }
  };

  return (
    <div className="new-message-container">
      <h2>Nowa Wiadomość</h2>
      {error && <div className="error-msg">{error}</div>}
      {successMessage && <div className="success-msg">{successMessage}</div>}
      <label className="message-label">
        Odbiorca:
        <input
          type="text"
          list="users"
          value={selectedRecipient}
          onChange={(e) => setSelectedRecipient(e.target.value)}
        />
        <datalist id="users">
          {filteredUsers.map((user) => (
            <option key={user.id} value={user.username} />
          ))}
        </datalist>
      </label>
      <br />
      <label className="message-label">
        Treść:
        <textarea
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
      </label>
      <br />
      <button className="send-btn" onClick={handleSendMessage}>
        Wyślij
      </button>
      <button className="back-btn" onClick={() => navigate("/dashboard")}>
        Powrót
      </button>
    </div>
  );
};

export default NewMessage;
