import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./NewMessage.scss";

const NewMessage = ({ userId }) => {
  const [selectedRecipient, setSelectedRecipient] = useState("");
  const [content, setContent] = useState("");
  const [successMessage, setSuccessMessage] = useState(null);
  const [error, setError] = useState(null);
  const [usersList, setUsersList] = useState([]);
  const [token, setToken] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const sessionToken = sessionStorage.getItem("token");
    setToken(sessionToken);
  }, []);

  useEffect(() => {
    const fetchUsersList = async () => {
      try {
        const response = await axios.get("/api/users", { params: { token } });
        setUsersList(response.data.users);
      } catch (err) {
        setError("Błąd podczas pobierania listy użytkowników");
        console.error(err.response ? err.response.data : err.message);
      }
    };

    fetchUsersList();
  }, [token, setError]);

  const handleSendMessage = async () => {
    try {
      const recipient = selectedRecipient;

      if (!recipient) {
        setError("Nieprawidłowy odbiorca");
        return;
      }

      const recipientUser = usersList.find(
        (user) => user.username === recipient
      );

      if (!recipientUser) {
        setError("Nieprawidłowy odbiorca");
        return;
      }

      await axios.post("/api/messages", {
        token,
        sender: userId,
        recipient: recipientUser.email,
        content,
      });

      setSuccessMessage("Wiadomość została wysłana pomyślnie.");
    } catch (err) {
      setError("Błąd podczas wysyłania wiadomości");
      console.error(err.response ? err.response.data : err.message);
    }
  };

  return (
    <div className="new-message-container">
      <label className="message-label">
        Odbiorca:
        <input
          type="text"
          list="users"
          value={selectedRecipient}
          onChange={(e) => setSelectedRecipient(e.target.value)}
        />
        <datalist id="users">
          {/* Użyj dostępnych odbiorców zamiast filteredUsers */}
          {usersList.map((user) => (
            <option key={user.username} value={user.username} />
          ))}
        </datalist>
      </label>
      <label className="message-label">
        Treść:
        <textarea
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
      </label>
      <br />
      <button className="send-btn" onClick={handleSendMessage}>
        Wyślij
      </button>
      <button className="back-btn" onClick={() => navigate("/dashboard")}>
        Powrót
      </button>

      {successMessage && <p className="success-message">{successMessage}</p>}
      {error && <p className="error-message">{error}</p>}
    </div>
  );
};

export default NewMessage;
