// components/NewMessage.jsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Zmiana importu na useNavigate
import axios from "axios";

const NewMessage = ({ userId }) => {
  const [recipient, setRecipient] = useState("");
  const [content, setContent] = useState("");
  const [successMessage, setSuccessMessage] = useState(null);
  const navigate = useNavigate(); // Zmiana na useNavigate

  const handleSendMessage = async () => {
    try {
      await axios.post(`/users/${userId}/messages/send`, {
        sender: userId,
        recipient,
        content,
      });

      // Ustawienie komunikatu o sukcesie
      setSuccessMessage("Wiadomość została wysłana pomyślnie.");

      // Przekierowanie użytkownika na stronę Dashboard po kilku sekundach
      setTimeout(() => {
        navigate("/dashboard");
      }, 3000); // Przekierowanie po 3 sekundach (możesz dostosować czas oczekiwania)
    } catch (error) {
      console.error("Błąd podczas wysyłania wiadomości:", error);
    }
  };

  return (
    <div>
      <h2>Nowa Wiadomość</h2>
      {successMessage && <div style={{ color: "green" }}>{successMessage}</div>}
      <label>
        Odbiorca:
        <input
          type="text"
          value={recipient}
          onChange={(e) => setRecipient(e.target.value)}
        />
      </label>
      <br />
      <label>
        Treść:
        <textarea
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
      </label>
      <br />
      <button onClick={handleSendMessage}>Wyślij</button>
    </div>
  );
};

export default NewMessage;
